class InstallationError(Exception):
    """Represents a failure to install a given program."""
    program = None

    def __init__(self, program):
        """`program` is the program that could not be installed.

        >>> raise InstallationError('Freshen')
        Traceback (most recent call last):
        ...
        InstallationError: Freshen could not be installed.
        """
        self.program = program

    def __str__(self):
        return self.program + " could not be installed."


class CacheWarning(UserWarning):
    """Represents a failure loading data from cache."""
    pass


if __name__ == '__main__':
    import doctest
    if doctest.testmod()[0]:
        exit(1)
